return items.map(item => {
  const start = new Date(item.json.start.dateTime);
  const end = new Date(item.json.end.dateTime);

  // --- Calculate duration ---
  const diffMs = end - start;
  const diffMinutes = diffMs / (1000 * 60);
  const diffHours = diffMinutes / 60;

  // --- Get readable day ---
  const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
  const meetingDay = start.toLocaleDateString('en-US', options); 

  // --- Extract attendees ---
  const attendees = (item.json.attendees || []).map(a => a.email);
  const attendeesList = attendees.join(', '); // comma-separated for easy use in Docs or email

  // --- Add all computed fields ---
  item.json.durationMinutes = diffMinutes;
  item.json.durationHours = parseFloat(diffHours.toFixed(2));
  item.json.meetingDay = meetingDay;
  item.json.attendeeEmails = attendees;
  item.json.attendeeList = attendeesList;

  return item;
});

